DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `category_id` int(11) NOT NULL DEFAULT '0' COMMENT '所属分类id',
  `model_id` int(11) NOT NULL DEFAULT '0' COMMENT '所属模型id',
  `user_id` int(11) NOT NULL DEFAULT '0' COMMENT '作者',
  `title` varchar(200) NOT NULL DEFAULT '' COMMENT '文章标题',
  `keywords` varchar(40) DEFAULT NULL COMMENT '关键词',
  `description` mediumtext COMMENT '摘要',
  `seo_title` mediumtext COMMENT 'SEO标题',
  `seo_description` mediumtext COMMENT 'SEO描述',
  `seo_keywords` mediumtext COMMENT 'SEO关键词',
  `views` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',
  `show_pic` varchar(200) NOT NULL DEFAULT '' COMMENT '配图',
  `show_pic_m` varchar(200) NOT NULL DEFAULT '' COMMENT '中等图',
  `show_pic_s` varchar(200) NOT NULL DEFAULT '' COMMENT '配图的缩略图',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `is_hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否热推',
  `is_new` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否最新',
  `is_carousel` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否在轮播图显示',
  `create_time` char(10) NOT NULL DEFAULT '' COMMENT '创建时间',
  `modify_time` char(10) NOT NULL DEFAULT '' COMMENT '修改时间',
  `display_time` char(10) NOT NULL DEFAULT '' COMMENT '显示时间',
  `comment_checked` int(11) NOT NULL DEFAULT '0' COMMENT '已读评论数目',
  `comment_unchecked` int(11) NOT NULL DEFAULT '0' COMMENT '未读评论数目',
  `allow_comment` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否允许评论',
  `reprint_title` varchar(50) NOT NULL DEFAULT '' COMMENT '转载标题',
  `reprint_address` varchar(200) NOT NULL DEFAULT '' COMMENT '转载地址',
  `praise_count` int(11) NOT NULL DEFAULT '0' COMMENT '赞的次数',
  `stamp_count` int(11) NOT NULL DEFAULT '0' COMMENT '踩的次数',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '文章状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=1592 DEFAULT CHARSET=utf8 COMMENT='主内容表';

