INSERT INTO `posts` VALUES ('1451', '19', '2', '1', '黄梅名家-韩再芬', '黄梅名家,韩再芬', '韩再芬，中国黄梅戏表演艺术家，国家级非物质文化遗产项目代表性传承人。中国戏剧家协会副主席、安徽省戏剧家协会副主席、国家一级演员，第五届、六届、七届全国文艺工作者代表大会代表。第四届、五届全国戏剧家代表大会代表。现任安庆再芬黄梅艺术剧院院长，第十届、十一届、十二届全国人大代表，安徽省政协常委。', '', '', '', '5039', '/files/images/origin/20150116/142141381461.jpg', '/files/images/middle/20150116/142141381461.jpg', '/files/images/thumbs/20150116/142141381461.jpg', '0', '1', '1', '1', '1420697866', '1421414083', '1420675200', '0', '0', '1', '黄梅戏吧', 'http://tieba.baidu.com/黄梅戏', '3', '0', '1');
