INSERT INTO `posts` VALUES ('1453', '9', '1', '1', '王彩云11日办京胡演奏会 李维康等名家将助阵', '王彩云,京胡', '著名京剧琴师王彩云京胡演奏会将在国家大剧院上演，李维康、耿其昌、于魁智等名家将助阵。回忆学艺往事，王彩云说：“在艺术道路上克隆别人永远不是自己，希望汲取百家之长形成自己的风格。”', '', '', '', '15', '/files/images/origin/20150115/142132878351.jpg', '/files/images/middle/20150115/142132878351.jpg', '/files/images/thumbs/20150115/142132878351.jpg', '0', '0', '0', '0', '1420775340', '1421328787', '1420761600', '0', '0', '1', '搜狐娱乐', 'http://yule.sohu.com/20150109/n407661434.shtml', '3', '0', '1');
INSERT INTO `posts` VALUES ('1454', '22', '1', '1', '阳泉文广新局举办“戏曲剧目赏析”专题讲座 ', '戏曲剧目赏析', '市文广新局举办了“戏曲剧目赏析”专题讲座。各县区及市直文艺单位和艺创中心戏剧组的分管领导、业务骨干、创作人员等60余人参加培训。', '', '', '', '5003', '', '', '', '0', '0', '0', '0', '1420777577', '1420777577', '1420761600', '0', '0', '1', '中国网', 'http://jjsx.china.com.cn/lm2595/2015/285684.htm', '0', '0', '1');
INSERT INTO `posts` VALUES ('1455', '22', '1', '1', '2015戏曲交响乐新春音乐会即将开幕', '2015，戏曲交响乐，新春音乐会', '深圳大型创新文化公益项目“戏曲交响乐”自2014年启动以来，受到广大高雅艺术爱好者的青睐。记者昨日了解到，为了迎接即将到来的农历新年，戏曲交响乐将携深圳交响乐团于1月11日举办新春音乐会暨第二届戏曲交响乐开幕式，为市民送上视听文化大餐。', '', '', '', '5002', '', '', '', '0', '0', '0', '0', '1420777892', '1420777892', '1420761600', '0', '0', '1', '光明网', 'http://culture.gmw.cn/newspaper/2015-01/08/content_103571677.htm', '0', '0', '1');
